public class RightTriangle {
    public static void main(String[] args)
    {
      System.out.println("@");
      for(int i=0;i<3;i++)
      {
        
          System.out.print("@");
          for(int j=0;j<=i;j++)
          {
              System.out.print(" ");
          }
          System.out.println("@");
      }
      for(int i=0;i<5;i++)
      {
          System.out.print("@");
      }
       
        System.out.println();


      //next Pattern

         for(int i=1;i<=5;i++)
         {
                System.out.print("*");
         }
         System.out.println();
             for(int j=1;j<2;j++)
             {  
                 System.out.print("*");
                 for(int k=1;k<=3;k++)
                 {
                 System.out.print(" ");
                 }
                 System.out.println("*");
             }
             for(int i=1;i<=5;i++)
             {
                    System.out.print("*");
             }
             
             System.out.println();
             for(int j=1;j<=2;j++)
             {  
                 System.out.print("*");
                 for(int k=1;k<=3;k++)
                 {
                 System.out.print(" ");
                 }
                 System.out.println("*");
             }

         }

    }
    

